ancestral = Category.create(name: "Ancestral", definition: "Composed of a base spirit lightly adorned with sugar (in Improved versions, the sweetener appears in the form of a dash or two of liqueur such as maraschino or curacao), bitters, and water (usually in its frozen form), and served either straight up or on the rocks.", popular_drinks: [{ :name => "Old Fashioned", :image => "#{ENV["HOST_URL"]}/photos/cocktails/old-fashioned.jpg" }, { :name => "Sazerac", :image => "#{ENV["HOST_URL"]}/photos/cocktails/sazerac-768x768.jpg" }])

duos_and_trios = Category.create(name: "Duos and Trios", definition: "Duos are composed of two ingredients, usually a base spirit and a liqueur, and are normally served in an old-fashioned glass. Trios are duos to which cream or a cream liqueur has been added. Not all duos can be made into trios. Not all trios start out as duos", popular_drinks: [{ name: "Godfather", image: "#{ENV["HOST_URL"]}/photos/cocktails/godfather-cocktail.jpg" }, { name: "White Russian", image: "#{ENV["HOST_URL"]}/photos/cocktails/white_russian.jpg" }])

