punch = Category.create(name: "Punch", definition: "Refers to a variety of festive drinks, non-alcoholic or alcoholic, generally containing fruits and fruit juices, spirits and liqueurs if alcoholic, and often topped with sparkling wine or soda. It is usually served at parties in large, wide bowls, known as punch bowls.", popular_drinks: [{ name: "Gin Punch", image: "#{ENV["HOST_URL"]}/photos/cocktails/Gin-punch-epicurious.webp" }, { name: "Tequila Watermelon Punch", image: "#{ENV["HOST_URL"]}/photos/cocktails/Tequila-watermelon-punch.jpg" }])

