margarita = Cocktail.create(name: "Margarita", image: "#{ENV["HOST_URL"]}/photos/cocktails/Margarita.jpg", description: "The Margarita is one of the most popular cocktails in North America—for good reason. Combining the tang of lime and the sweetness of orange liqueur with the distinctive strength of tequila, our classic Margarita strikes all of the right keys.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a rocks glass over fresh ice. Garnish with a lime wheel.", ingredients: ["2 oz Tequila", "1 oz Lime Juice", "0.75 oz Orange Liqueur"], bartender_id: jamie.id, category_id: new_orleans_sour.id)
jungle_bird = Cocktail.create(name: "Jungle Bird", image: "#{ENV["HOST_URL"]}/photos/cocktails/Jungle-Bird.jpg", description: "The Jungle Bird is a modern classic tiki cocktail that was created in 1978 at the Aviary Bar in Kuala Lumpur, Malaysia. Its a tropical, tiki cocktail thats a little bitter, a little sweet, and a little strong.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a rocks glass over fresh ice. Garnish with a lime wheel.", ingredients: ["1.5 oz Dark Rum", "0.75 oz Campari", "0.5 oz Lime Juice", "0.5 oz Simple Syrup", "1.5 oz Pineapple Juice"], bartender_id: toggie.id, category_id: tiki.id)
brooklyn = Cocktail.create(name: "Brooklyn", image: "#{ENV["HOST_URL"]}/photos/cocktails/Brooklyn.jpg", description: "The Brooklyn cocktail is a classic drink made with rye whiskey, dry vermouth, maraschino liqueur, and Amer Picon. It's a boozy, spirit-forward cocktail that's easy to make and a great alternative to a Manhattan.", execution: "Add all ingredients into a mixing glass with ice and stir until well-chilled. Strain into a chilled cocktail glass. Garnish with a cherry.", ingredients: ["2 oz Rye Whiskey", "0.75 oz Dry Vermouth", "0.25 oz Maraschino Liqueur", "0.25 oz Amer Picon"], bartender_id: jack.id, category_id: french_italian.id)
old_fashioned = Cocktail.create(name: "Old Fashioned", image: "#{ENV["HOST_URL"]}/photos/cocktails/old-fashioned.jpg", description: "The Old Fashioned is a cocktail made by muddling sugar with bitters and water, adding whiskey or, less commonly, brandy, and garnishing with orange slice or zest and a cocktail cherry. It is traditionally served in an old fashioned glass, which predated the cocktail.", execution: "Add sugar cube to old fashioned glass and saturate with bitters, add a dash of plain water. Muddle until dissolved. Fill the glass with ice cubes and add whiskey. Garnish with orange twist, and a cocktail cherry.", ingredients: ["2 oz Bourbon or Rye Whiskey", "1 Sugar Cube", "2 Dashes Angostura Bitters", "1 Dash Plain Water"], bartender_id: doug.id, category_id: ancestral.id)
daiquiri = Cocktail.create(name: "Daiquiri", image: "#{ENV["HOST_URL"]}/photos/cocktails/daiquiri.webp", description: "The Daiquiri is a classic rum cocktail that is simple, refreshing and timeless. The balance of sweet, sour and spirit is perfectly balanced and the drink can be easily modified to suit your taste.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass.", ingredients: ["2 oz White Rum", "0.75 oz Lime Juice", "0.75 oz Simple Syrup"], bartender_id: doug.id, category_id: simple_sour.id)
negroni = Cocktail.create(name: "Negroni", image: "#{ENV["HOST_URL"]}/photos/cocktails/negroni.jpg", description: "The Negroni is a popular Italian cocktail, made of one part gin, one part vermouth rosso, and one part Campari, garnished with orange peel. It is considered an apéritif.", execution: "Add all ingredients into mixing glass with ice and stir until well-chilled. Strain into chilled cocktail glass. Garnish with an orange peel.", ingredients: ["1 oz Gin", "1 oz Campari", "1 oz Sweet Vermouth"], bartender_id: jamie.id, category_id: french_italian.id)
whiskey_sour = Cocktail.create(name: "Whiskey Sour", image: "#{ENV["HOST_URL"]}/photos/cocktails/whiskey_sour.jpg", description: "The whiskey sour is a mixed drink containing whiskey, lemon juice and simple syrup. It is shaken and served either straight or over ice. The traditional garnish is half an orange slice and a maraschino cherry.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a rocks glass over fresh ice. Garnish with a cherry and an orange slice.", ingredients: ["2 oz Bourbon Whiskey", "0.75 oz Lemon Juice", "0.75 oz Simple Syrup"], bartender_id: jack.id, category_id: simple_sour.id)
clover_club = Cocktail.create(name: "Clover Club", image: "#{ENV["HOST_URL"]}/photos/cocktails/Clover-club-cocktail-5.jpg", description: "The Clover Club is a cocktail consisting of gin, lemon juice, raspberry syrup, and an egg white. It is traditionally served in a cocktail glass, but may also be served in a Collins glass or an Old Fashioned glass without ice.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a raspberry.", ingredients: ["2 oz Gin", "0.75 oz Lemon Juice", "0.75 oz Raspberry Syrup", "1 Egg White"], bartender_id: toggie.id, category_id: enhanced_sour.id)
white_russian = Cocktail.create(name: "White Russian", image: "#{ENV["HOST_URL"]}/photos/cocktails/white_russian.jpg", description: "The White Russian is a cocktail made with vodka, coffee liqueur and cream served with ice in an old fashioned glass. Often milk will be used as an alternative to cream.", execution: "Add all ingredients into old fashioned glass filled with ice. Stir gently.", ingredients: ["2 oz Vodka", "1 oz Coffee Liqueur", "1 oz Fresh Cream"], bartender_id: jack.id, category_id: duos_and_trios.id)
aviation = Cocktail.create(name: "Aviation", image: "#{ENV["HOST_URL"]}/photos/cocktails/aviation.jpg", description: "The Aviation is a classic cocktail made with gin, maraschino liqueur, crème de violette, and lemon juice. Some recipes omit the crème de violette. It is served straight up, in a cocktail glass.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a cherry.", ingredients: ["2 oz Gin", "0.5 oz Maraschino Liqueur", "0.5 oz Crème de Violette", "0.75 oz Lemon Juice"], bartender_id: jamie.id, category_id: international_sour.id)
gun_metal_blue = Cocktail.create(name: "Gun Metal Blue", image: "#{ENV["HOST_URL"]}/photos/cocktails/gunmetal_blue.jpg", description: "The Gun Metal Blue is a cocktail made with gin, blue curaçao, lemon juice, and simple syrup. It is served straight up, in a cocktail glass.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a lemon twist.", ingredients: ["2 oz Gin", "0.5 oz Blue Curaçao", "0.75 oz Lemon Juice", "0.75 oz Simple Syrup"], bartender_id: toggie.id, category_id: international_sour.id)
blood_mary = Cocktail.create(name: "Blood Mary", image: "#{ENV["HOST_URL"]}/photos/cocktails/bloody-mary.webp", description: "The Bloody Mary is a cocktail containing vodka, tomato juice, and other spices and flavorings including Worcestershire sauce, hot sauces, garlic, herbs, horseradish, celery, olives, salt, black pepper, lemon juice, lime juice and celery salt.", execution: "Add all ingredients into highball glass filled with ice. Stir gently. Garnish with celery stalk and lemon wedge.", ingredients: ["1.5 oz Vodka", "3 oz Tomato Juice", "0.5 oz Lemon Juice", "2 Dashes Worcestershire Sauce", "3 Dashes Tabasco Sauce", "1 Pinch Celery Salt", "1 Pinch Black Pepper"], bartender_id: doug.id, category_id: snapper.id)
french_75 = Cocktail.create(name: "French 75", image: "#{ENV["HOST_URL"]}/photos/cocktails/French-75-Sipsmith.jpg", description: "The French 75 is a cocktail made from gin, Champagne, lemon juice, and sugar. It is also called a 75 Cocktail, or in French simply a Soixante Quinze.", execution: "Add gin, syrup and lemon juice to a shaker with ice and shake. Strain into a chilled champagne flute. Top with champagne. Garnish with a lemon twist.", ingredients: ["1.5 oz Gin", "0.5 oz Lemon Juice", "0.5 oz Simple Syrup", "2 oz Champagne"], bartender_id: jack.id, category_id: sparkling_sour.id)
ramos_gin_fizz = Cocktail.create(name: "Ramos Gin Fizz", image: "#{ENV["HOST_URL"]}/photos/cocktails/Ramos-Gin-Fizz-Tonique.jpg", description: "The Ramos gin fizz is a cocktail made with gin, lemon juice, lime juice, egg white, sugar, cream, orange flower water, and soda water. It is served in a large non-tapered 12 to 14 ounce Collins glass.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled highball glass without ice. Top with soda water. Garnish with a lemon twist.", ingredients: ["1.5 oz Gin", "0.5 oz Lemon Juice", "0.5 oz Lime Juice", "1 oz Simple Syrup", "1 Egg White", "1 oz Cream", "3 Dashes Orange Flower Water", "2 oz Soda Water"], bartender_id: jamie.id, category_id: sparkling_sour.id)
B52 = Cocktail.create(name: "B-52", image: "#{ENV["HOST_URL"]}/photos/cocktails/B52-Shooter.jpg", description: "The B-52 is a layered shot composed of a coffee liqueur, an Irish cream, and a Grand Marnier. When prepared properly, the ingredients separate into three distinctly visible layers.", execution: "Layer ingredients into a shot glass in the following order: Kahlua, Baileys, Grand Marnier.", ingredients: ["0.5 oz Kahlua", "0.5 oz Baileys", "0.5 oz Grand Marnier"], bartender_id: toggie.id, category_id: pousse.id)
cosmo = Cocktail.create(name: "Cosmopolitan", image: "#{ENV["HOST_URL"]}/photos/cocktails/cosmo.jpg", description: "A cosmopolitan, or informally a cosmo, is a cocktail made with vodka, triple sec, cranberry juice, and freshly squeezed or sweetened lime juice.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a lime wheel.", ingredients: ["1.5 oz Vodka Citron", "0.5 oz Cointreau", "0.5 oz Lime Juice", "1 oz Cranberry Juice"], bartender_id: jack.id, category_id: new_orleans_sour.id)
corpse_reviver = Cocktail.create(name: "Corpse Reviver no.2", image: "#{ENV["HOST_URL"]}/photos/cocktails/corpse_reviver.webp", description: "The Corpse Reviver family of cocktails are sometimes drunk as alcoholic hangover 'cures', of potency or characteristics to tongue in cheek be able to revive even a dead person. The corpse reviver family includes number 1, number 2, number 3, number 4, and number 5.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a lemon twist.", ingredients: ["0.75 oz Gin", "0.75 oz Cointreau", "0.75 oz Lillet Blanc", "0.75 oz Lemon Juice", "1 Dash Absinthe"], bartender_id: doug.id, category_id: new_orleans_sour.id)
vesper_martini = Cocktail.create(name: "Vesper Martini", image: "#{ENV["HOST_URL"]}/photos/cocktails/lillet-vesper-martini.webp", description: "The Vesper or Vesper Martini is a cocktail that was originally made of gin, vodka, and Kina Lillet. The formulations of its ingredients have changed since its original publication in print, and so some modern bartenders have created new versions which attempt to more closely mimic the original taste.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a lemon twist.", ingredients: ["1.5 oz Gin", "0.5 oz Vodka", "0.25 oz Lillet Blanc"], bartender_id: jack.id, category_id: french_italian.id)
boston_flip = Cocktail.create(name: "Boston Flip", image: "#{ENV["HOST_URL"]}/photos/cocktails/boston-flip.jpeg", description: "The Boston Flip is a cocktail made with rum, port, whole egg, simple syrup, and nutmeg. It is served in a cocktail glass.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with grated nutmeg.", ingredients: ["1.5 oz Dark Rum", "0.5 oz Port", "1 Whole Egg", "0.5 oz Simple Syrup"], bartender_id: toggie.id, category_id: flips_and_nogs.id)
brandy_flip = Cocktail.create(name: "Brandy Flip", image: "#{ENV["HOST_URL"]}/photos/cocktails/Brandy-Flip-2.jpg", description: "The Brandy Flip is a cocktail made with brandy, whole egg, simple syrup, and nutmeg. It is served in a cocktail glass.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with grated nutmeg.", ingredients: ["1.5 oz Brandy", "1 Whole Egg", "0.5 oz Simple Syrup"], bartender_id: jack.id, category_id: flips_and_nogs.id)
mint_julep = Cocktail.create(name: "Mint Julep", image: "#{ENV["HOST_URL"]}/photos/cocktails/Mint_Julep.jpg", description: "The mint julep is a cocktail consisting primarily of bourbon, sugar, water, crushed or shaved ice, and fresh mint. As a bourbon-based cocktail, it is associated with the American South and the cuisine of the Southern United States in general, and the Kentucky Derby in particular.", execution: "Add mint leaves, simple syrup and water into a julep cup or rocks glass. Gently muddle the mint, being careful not to over muddle. Fill the glass with crushed ice. Add bourbon and stir until the glass is frosted. Top with more crushed ice to form an ice dome and garnish with a mint sprig.", ingredients: ["2 oz Bourbon Whiskey", "0.5 oz Simple Syrup", "2 tsp Water", "4 Mint Leaves"], bartender_id: doug.id, category_id: juleps_and_smashes.id)
bronx_cocktail = Cocktail.create(name: "Bronx Cocktail", image: "#{ENV["HOST_URL"]}/photos/cocktails/bronx_cocktail.jpg", description: "The Bronx Cocktail is a cocktail made with gin, sweet vermouth, dry vermouth, and orange juice. It is served shaken and chilled, often with a slice of orange or a maraschino cherry.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with an orange twist.", ingredients: ["1.5 oz Gin", "0.5 oz Sweet Vermouth", "0.5 oz Dry Vermouth", "1 oz Orange Juice"], bartender_id: jack.id, category_id: enhanced_sour.id)
DarkNStormy = Cocktail.create(name: "Dark and Stormy", image: "#{ENV["HOST_URL"]}/photos/cocktails/DarkNStormy.png", description: "The Dark 'n' Stormy is a highball cocktail made with dark rum and ginger beer served over ice and garnished with a slice of lime. Lime juice and simple syrup are also frequently added.", execution: "Add rum and ginger beer into highball glass filled with ice and stir. Garnish with a lime wedge.", ingredients: ["2 oz Dark Rum", "3 oz Ginger Beer"], bartender_id: doug.id, category_id: highball.id)
frozen_pina_colada = Cocktail.create(name: "Frozen Pina Colada", image: "#{ENV["HOST_URL"]}/photos/cocktails/Frozen-pina-colada.jpg", description: "The piña colada is a sweet cocktail made with rum, cream of coconut or coconut milk, and pineapple juice, usually served either blended or shaken with ice. It may be garnished with either a pineapple wedge, maraschino cherry, or both.", execution: "Add all ingredients into a blender with ice and blend until smooth. Pour into chilled glass, garnish with a pineapple wedge and maraschino cherry.", ingredients: ["1.5 oz White Rum", "2 oz Pineapple Juice", "1 oz Coconut Cream"], bartender_id: jack.id, category_id: frozen.id)
hemingway_daiquiri = Cocktail.create(name: "Hemingway Daiquiri", image: "#{ENV["HOST_URL"]}/photos/cocktails/hemingway_daiquiri.jpeg", description: "The Hemingway Daiquiri, also known as the Papa Doble, is a cocktail that supposedly was favored by Ernest Hemingway. It is a variation of the classic Daiquiri that consists of white rum, maraschino liqueur, lime juice, and grapefruit juice.", execution: "Add all ingredients into a shaker with ice and shake. Strain into a chilled cocktail glass. Garnish with a lime wheel.", ingredients: ["2 oz White Rum", "0.75 oz Lime Juice", "0.5 oz Maraschino Liqueur", "0.5 oz Grapefruit Juice"], bartender_id: jamie.id, category_id: international_sour.id)
irish_coffee = Cocktail.create(name: "Irish Coffee", image: "#{ENV["HOST_URL"]}/photos/cocktails/irish-coffee.jpg", description: "Irish coffee is a cocktail consisting of hot coffee, Irish whiskey, and sugar, stirred, and topped with cream. The coffee is drunk through the cream.", execution: "Heat the coffee, whiskey and sugar; do not boil. Pour into glass and top with cream; serve hot.", ingredients: ["1.5 oz Irish Whiskey", "1 tsp Brown Sugar", "6 oz Hot Coffee", "Heavy Cream"], bartender_id: doug.id, category_id: toddy.id)
hot_buttered_rum = Cocktail.create(name: "Hot Buttered Rum", image: "#{ENV["HOST_URL"]}/photos/cocktails/Hot-buttered-rum.jpg", description: "Hot buttered rum is a mixed drink containing rum, butter, hot water or cider, a sweetener, and various spices. It is especially popular in the fall and winter and is traditionally associated with the holiday season.", execution: "Combine butter, sugar, and spices in a mug. Pour in rum and top with boiling water. Stir well.", ingredients: ["2 oz Dark Rum", "2 tsp Brown Sugar", "1 tsp Unsalted Butter", "1 Pinch Ground Cinnamon", "1 Pinch Ground Nutmeg", "5 oz Boiling Water"], bartender_id: jack.id, category_id: toddy.id)
